static int X= 0, Y= 1, R= 2;

// brute force
public int countLatticePoints(int[][] circles) {
	// 1. sort circles from largest to smallest (heuristic)
	Arrays.sort(circles, (c1, c2)->Integer.compare(c2[R],c1[R]));

    // 2. find the smallest rectangle containing all circles
	int minX= Integer.MAX_VALUE, maxX= Integer.MIN_VALUE;
	int minY= Integer.MAX_VALUE, maxY= Integer.MIN_VALUE;
	for(int[] c:circles){
		minX= Math.min(minX, c[X]-c[R]);
		maxX= Math.max(maxX, c[X]+c[R]);
		minY= Math.min(minY, c[Y]-c[R]);
		maxY= Math.max(maxY, c[Y]+c[R]);
	}
	// 3. for all lattice points in the rectangle check if at least one circle contains them
	int count= 0;
	for(int x=minX; x<=maxX; x++){
		for(int y=minY; y<=maxY; y++){
			for(int[] c:circles){
				int rsq= c[R]*c[R];
				int dx= Math.abs(c[X]-x), dy= Math.abs(c[Y]-y);
				if(dx*dx+dy*dy<=rsq){
					count++;
					break;
				}
			}           
		}
	}
	return count;
}

