class Solution {
public:
    int countLatticePoints(vector<vector<int>>& circles) {
        int minX{INT_MAX}, minY{INT_MAX}, maxX{}, maxY{}, ct{};

		// Find bounding box containing all circles
        for(const auto& circle : circles)
        {
            minX = std::min(minX, circle[0] - circle[2]);
            minY = std::min(minY, circle[1] - circle[2]);
            maxX = std::max(maxX, circle[0] + circle[2]);
            maxY = std::max(maxY, circle[1] + circle[2]);
        }

		// Sweep through the bounding box containing all circles
        for(int x = minX; x <= maxX; x++)
        {
            for(int y = minY; y <= maxY; y++)
            {
                for(const auto& circle : circles)
                {
                    int distX{circle[0] - x}, distY{circle[1] - y};
                    if(circle[2] * circle[2] < distX * distX + distY * distY)
                        continue;
                    ct++;
                    break;
                }
            }
        }

        return ct;
    }
};
