class Solution {
    public int countLatticePoints(int[][] circles) {
        Set<Pair<Integer, Integer>> res = new HashSet<>(); //We need a set to avoid the duplicate
        for (int i = 0; i < circles.length; i++){
            int cx = circles[i][0]; //x of the center of the circle
            int cy = circles[i][1]; //y of the center of the circle
            int r = circles[i][2]; // radius of the circle
            int xl = cx - r, xh = cx + r; //low and high boundaries for x
            int yl = cy - r, yh = cy + r; //low and high boundaries for y

            for (int j = xl; j <= xh; j++){
                for (int k = yl; k <= yh; k++){
                    int xd = Math.abs(cx - j); //x distance from the center
                    int yd = Math.abs(cy - k); //y distance from the center

                    //if it is on or inside of the circle, add to the set
                    if (xd * xd + yd * yd <= r * r){

                        //if we use, new int[j,k], it won't work because it creates a new object, so we use pair. Alternatively we can use String like 'j_k' so that the set doesn't add any duplicate
                        Pair<Integer, Integer> pair = new Pair<>(j,k);
                        res.add(pair);
                    }
                }
            }
        }
        return res.size();
    }
}
