  def countLatticePoints(circles: Array[Array[Int]]): Int = {
    import scala.collection.mutable
    def getBounds(x: Int, y: Int, r: Int): Seq[(Int, Int)] = {
      for {
        i <- (x - r to x + r)
        j <- (y - r to y + r) if ((i - x) * (i - x)) + ((j - y) * (j - y)) <= r * r
      } yield (i, j)
    }

    circles.foldLeft(mutable.HashSet.empty[(Int, Int)]){
      case (hash, Array(x, y, r)) => hash.addAll(getBounds(x, y, r))
    }.size
