import math
class Solution:
    def countLatticePoints(self, circles) :

        point = {}

        for c in circles:

            xc = c[0]
            yc = c[1]
            r = c[2]
            xmin = xc - r
            xmax = xc + r
            ymin = yc - r
            ymax = yc + r

            for x in range(xmin, xmax + 1):
                for y in range(ymin, ymax + 1):

                    t = (x, y)
                    if t in point:
                        continue
                    if ((x==xmax or x==xmin)and(y==ymax or y==ymin)):
                            continue
                    else:
                            dist = math.sqrt((x - xc) ** 2 + (y - yc) ** 2)
                            if dist <= r:
                                point[t]=1


        return len((point))


