class Solution {
    public String removeDigit(String number, char digit) {
        String ans = "";
        for(int i = 0; i < number.length(); ++i){
            if(number.charAt(i) == digit){
                String actual = number.substring(0, i) + number.substring(i+1);
                if(checkGreater(ans, actual))
                    ans = actual;
            }
        }
        return ans;
    }
    private boolean checkGreater(String ans, String actual){
        for(int i = 0;; i < Math.min(ans.length(), actual.length()); i++){
            if(actual.charAt(i) > ans.charAt(i)) return true;
            else if(actual.charAt(i) < ans.charAt(i)) return false;
        }
        return true;
    }
}

