var removeDigit = function(number, digit) {
    let digitIdx = -1;
    for (let i = 0; i < number.length; i++) {
        if (number[i] === digit) {
            digitIdx = i;
            if (number[i + 1] > digit) {
                break;
            }
        }
    }
    return number.slice(0, digitIdx) + number.slice(digitIdx + 1);
};
