class Solution {

    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {
        Arrays.sort(tiles, (a, b) -> a[0] - b[0]); // sort on basis of start
        int n = tiles.length;
        int pref[] = new int[n];
        pref[0] = tiles[0][1] - tiles[0][0] + 1;
        for (int i = 1; i < n; i++) {
            pref[i] = pref[i - 1] + (tiles[i][1] - tiles[i][0] + 1); // prefix of white tiles
        }
        int ans = 0;
        for (int i = 0; i < tiles.length; i++) {
            int s = i, e = n - 1;
            int req = tiles[i][0] + carpetLen - 1; // max expansion from current start 
            int pos = i;
			//binary search to find req lies on which less than equal to range.
            while (s <= e) {
                int mid = s + (e - s) / 2;
                if (tiles[mid][0] > req) {
                    e = mid - 1;
                } else {
                    pos = mid;
                    s = mid + 1;
                }
            }
			// if ending of that last possible tile range is greater than maximum possible expansion
			// then cover only the required white tiles
            int ex = (tiles[pos][1] >= req) ? (req - tiles[pos][0] + 1) : (tiles[pos][1] - tiles[pos][0] + 1); 
			// all mid tiles will get covered anyhow 
            int sum = (pos - 1 >= 0 ? pref[pos - 1] : 0) - (i - 1 >= 0 ? pref[i - 1] : 0) + ex;
            ans = Math.max(ans, sum);
        }
        return ans;
    }
}
