class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        sort(tiles.begin(),tiles.end());
        int start=0;
        int end=0;
        int n=tiles.size();
        int whites=0;
        int ans=0;
        while(start<n && end<n){
            if(tiles[start][0]+carpetLen > tiles[end][1]){
                whites+=tiles[end][1]-tiles[end][0]+1;
                ans=max(ans,whites);
                end++;
            }
            else{
                int val=whites + max(0,tiles[start][0]+carpetLen-tiles[end][0]);
                //it is not neccessary that all white tiles of a particular section should come under the carpet. We can take only those whites that are coming under carpet
                //for example if carpet length is 10 and
                // (1,5) (10,11)
                //   s     e
                //now 1+10=11 which is  not gretaer than tiles[e][1]
                //so we cannot include both the tiles at 10 and 11 but we can include some tiles from them
                //as we can see 1+10 is 11 and tiles[e][0] is 10 which is less than the carpet coverage
                //so we will include the 10th tile in our range excluding the 11th one
                // val is doing this thing for us
                // tiles[start][0]+carpetLen-tiles[end][0] gives the white tiles that can be included in current range
                ans=max(ans,val);
                whites=whites-(tiles[start][1]-tiles[start][0]+1); //sliding the current window
                start++;

            }
        }
        return ans;
    }
};
