class Solution {
    public int binary_search( int[][] tiles, int left, int right, int val )
    {
        int ans = -1;
        while( left <= right )
        {
            int mid = left + (right-left)/2;

            if( tiles[mid][0] <= val && tiles[mid][1] >= val )
                return mid;
            if( tiles[mid][0] > val )
                right = mid-1;
            else
            {
                ans =  mid;
                left = mid+1;
            }
        }

        return ans;
    }

    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {
        int n = tiles.length;
        long[] totalTiles = new long[n];
        int ans = 0;

        Arrays.sort( tiles, (t1, t2) -> t1[0] - t2[0] );

