class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        //sort by tile start
        sort(tiles.begin(), tiles.end());

        // define endIdx i.e. index for tile chunk where the carpet ends.
        int ans = 0, total = 0, endIdx=0;
        for(int startIdx = 0;startIdx<tiles.size();startIdx++) {

            //align carpet with start of current tile chunk
            int start = tiles[startIdx][0];

            //given carpet length this is how far carpet end can go.
            int end = start + carpetLen -1;

            // search on which tile chunk in given array would be the end of carpet.
            // note: here for each startIdx we see how far endIdx can go further
            // there won't be any case where for a startIdx we will need to move endIdx backwards.
            while(endIdx<tiles.size() && tiles[endIdx][1]<end) {
                total += tiles[endIdx][1]-tiles[endIdx][0]+1;
                endIdx++;
            }

            // if we have reached the end of array or carpet ended somwhere between tile chunks.
            // then possible answer is total for this startIdx.
            if(endIdx==tiles.size() || tiles[endIdx][0] > end) {
                ans = max(ans, total);
            } else {
                // else if we only add tile chunk start to carpet end in our answer.
                ans = max(ans, total + (end - tiles[endIdx][0]+1));
            }

            //since we move to next startIdx in next iteration, remove tiles from current startIdx
            total -= tiles[startIdx][1] - tiles[startIdx][0] + 1;
        }

        return ans;
    }
};
