```c++
int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
    // Sort the input vector of tiles based on their starting position
    sort(tiles.begin(), tiles.end());

    // Initialize two variables: `got` to keep track of how many white tiles are currently "in hand",
    // and `res` to keep track of the maximum number of white tiles seen so far
    auto got = 0, res = 0;

    // Loop through the tiles vector, stopping when `res` is greater than or equal to `carpetLen` or
    // when we've processed all the tiles
    for (int i = 0, j = 0; res < carpetLen && i < tiles.size();) {

        // Get the start and end position of the current tile being considered
        auto start = tiles[i][0], end = tiles[i][1];

        // Calculate the position where the carpet ends
        auto to = tiles[j][0] + carpetLen;

        // If the carpet ends after the current tile, add the entire tile's length to `got`,
        // update `res` if needed, and move on to the next tile
        if (to > end) {
            got += end - start + 1;
            res = max(res, got);
            i++;
        }

        // If the carpet ends before the current tile, add the amount of the tile that is covered
        // by the carpet to `got`, update `res` if needed, subtract the entire length of the current
        // tile from `got`, and move on to the next tile
        else {
            auto partial = max(0, to - start);
            res = max(res, got + partial);
            got -= (tiles[j][1] - tiles[j][0] + 1);
            j++;
        }
    }

    // Return the maximum number of white tiles seen
    return res;
}
