    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {

        //Index to keep track of sliding window
        int start =0;
        int end=0;

        int currentCovered=0; //length covered by current window
        int maxCovered=0; //Max across all tiles

		//Sort tiles in ascending order
        Arrays.sort(tiles,(a,b)->(a[0]-b[0]));

        while(start<=end && end < tiles.length){

            int[] endTile = tiles[end];
            int[] startTile = tiles[start];

            //Check when picking end tile, if start to end is within carpetLen
            int currentWindowSize = endTile[1]-startTile[0]+1;
            if(currentWindowSize<=carpetLen){
                //Since its within carpetLen include end tile, and update end

			   int endTileLen = endTile[1]-endTile[0]+1;
                currentCovered= currentCovered+endTileLen;
                maxCovered = Math.max(maxCovered, currentCovered);
                end++;
            }else{
                //if it exceeds the carpetLen, then we can only cover partially, calculate partial value of end that can be covered
				//eg: start[0,5] end[6,10] with carpetLen 7, when picking end we need to pick [0,5] + [6,7] ie partially

				int endTilePartial = startTile[0] + carpetLen - endTile[0];
                maxCovered = Math.max(maxCovered, currentCovered+endTilePartial);

                //currentCovered+=endTilePartial; 
                //Note - Do not add partial to currentCovered, endTile will be completely picked in the next iteration once start is removed

                //remove start so we can keep extending end
                int startTileLen = startTile[1]-startTile[0]+1;
                currentCovered-= startTileLen;
                start++;                
            } 
        }
        return maxCovered;        
