        int ans=0;
        for(int i=0;i<n;i++)
        {
            int leftEnd=tiles[i][0];
            int rightEnd=tiles[i][0]+carpetLen-1;
            int l=i,r=n-1,left=i,right=i-1;
            int curr=0;
            while(l<=r)
            {
                int mid=(l+r)/2;
                if(tiles[mid][1]<=rightEnd)
                {
                    right=mid;
                    l=mid+1;
