class Solution {
    public int maximumWhiteTiles(int[][] tiles, int k) {
        Arrays.sort(tiles,(a,b)->a[0]-b[0]);
        int max=0,cnt=0,l=0;
        for(int r=0;r<tiles.length;r++) {
            // add all right tiles
            cnt+=tiles[r][1]-tiles[r][0]+1;
            int len=0;
            while(l<tiles.length&&(len=tiles[r][1]-tiles[l][0]+1)>k) {
                // keep removing tiles from left side
                max=Math.max(max,cnt-len+k);
                cnt-=tiles[l][1]-tiles[l][0]+1;
                l++;
            }
            max=Math.max(max,cnt);
            if(max==k) return k;
        }
        return max;
    }
}
