class Solution {
    public int maximumWhiteTiles(int[][] tiles, int len) {
        int result = 0, start = 0, covered = 0;
        Arrays.sort(tiles,(a,b)->(a[0]-b[0]));

        for(int end = 0; result<len&&end<tiles.length;){
            if(tiles[start][0]+len>tiles[end][1]){
                covered += Math.min(len, tiles[end][1] - tiles[end][0] + 1);
                result = Math.max(result,covered);
                end++;
            }else{
                int partial = Math.max(0,tiles[start][0] + len - tiles[end][0]);
                result = Math.max(result,covered+partial);
                covered -= (tiles[start][1]-tiles[start][0]+1);
                start++;
            }
        }
        return result;
    }
}
