    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {
        Arrays.sort(tiles, (a,b) -> a[0] - b[0]);  // Sort by start position
        int ans = 0, ci = 0, cj = 0, i = 0, j = 0;
        for(i = 0; i<tiles.length; i++){
            int covered = 0;
            ci = tiles[i][0]; // carpet start at tile start
            cj = ci+carpetLen;
            covered += (cj > tiles[i][1] ? tiles[i][1] - tiles[i][0] + 1 : cj-tiles[i][0]);
            for(j=i+1; j<tiles.length; j++){
                if(tiles[j][0] > cj) break;  //tile start > carpet end then break
                else covered += (cj > tiles[j][1] ? tiles[j][1] - tiles[j][0] + 1 : cj-tiles[j][0]); 
            }
            ans = Math.max(covered, ans);
            if(j==tiles.length) return ans;
        }
        return ans;
    }
}
