        Arrays.sort(tiles, new Comparator<int[]>(){public int compare(int[] t, int[] t1) {return t[0] - t1[0];}});

        int [] pfx = new int [tiles.length];
        //prefix sum of white tiles:
        pfx[0] = tiles[0][1] - tiles[0][0] + 1;
        for(int i = 1; i < tiles.length; i++) {
            pfx[i] = tiles[i][1] - tiles[i][0] + 1 + pfx[i-1];
        }

        //storing as key the ending position of a tile and the corresponding value is its index.
        TreeMap<Integer, Integer> t = new TreeMap<>();
        for(int i = 0; i < tiles.length; i++) 
            t.put(tiles[i][1], i);



        for(int i = 0; i < tiles.length; i++) {
            int curScore = tiles[i][1] - tiles[i][0] + 1, remaining = carpetLen - curScore;
            //Finding closest tile that cannot be entirely covered with the remaining carpet length.
            Entry<Integer, Integer> e = t.higherEntry(remaining + tiles[i][1]);
            int sum = 0;
            //if there is no such tile, it means we can cover all tiles starting from current tile.
            if(e == null) {
                sum += pfx[pfx.length - 1] - pfx[i];
            }
            else {
                //tiles before index e.getValue() are guaranteed to be entirely covered by the carpet.
                sum += pfx[e.getValue()- 1] - pfx[i];
                //rest stores the remaining length of the carpet after covering tiles [tiles[i]...tiles[e.getValue()-1]] inclusive.
                int rest = carpetLen - (tiles[e.getValue()][0] - tiles[i][0]);
                if(rest > 0) {
                    //if there is still remaining portion of the carpet, we can add it to the sum too.
                    sum += rest;
                }
            }
            result = Math.max(result, curScore + sum);
        }
        return result;
    }
