class Solution 
{
    public int maximumWhiteTiles(int[][] tiles, int carpetLen)
    {
        final int n = tiles.length;
        final long[] pref = new long[n + 1];

        if (n == 1) 
        {
            return Integer.min(carpetLen, tiles[0][1] - tiles[0][0] + 1);
        }

        Arrays.sort(tiles, (a, b) -> a[0] - b[0]);        

        for (int i = 1; i <= n; i++)
        {
            pref[i] = pref[i - 1] + (tiles[i - 1][1] - tiles[i - 1][0] + 1);
        }

        int ans = 0;
;
        for (int i = 0, j = 0; j < n; j++)
        {
            while(i <= j && tiles[j][1] - tiles[i][0] + 1 > carpetLen)
            {
                int mid = tiles[i][0] + carpetLen - 1;

                if (mid >= tiles[j][0] && mid <= tiles[j][1]) 
                {
                    ans = Integer.max(ans, (int) (pref[j] - pref[i]) + (mid - tiles[j][0] + 1));
                }

                i++;
            }

            ans = Integer.max(ans,  (int) (pref[j + 1] - pref[i]));
        }

        return ans;
    }
}
