/**
 * Time Complexity: O(n * log(n))
 * Space Complexity: O(n)
 * where `n` is the length of the vector `tiles`
 */
class Solution {
 public:
  int maximumWhiteTiles(vector<vector<int>> &tiles, const int carpetLen) {
    const int n = static_cast<int>(tiles.size());
    sort(tiles.begin(), tiles.end());
    int length_presums[n + 1];
    length_presums[0] = 0;
    for (int i = 0; i < n; ++i) {
      length_presums[i + 1] = length_presums[i] + (tiles[i].back() - tiles[i].front() + 1);
    }

    int ret = 0;
    for (int i = 0; i < n; ++i) {
      const int start = tiles[i].front();
      const int end = start + carpetLen - 1;
      auto ub = upper_bound(tiles.begin() + i, tiles.end(), vector<int>{end, numeric_limits<int>::max()});
      const int last_index = static_cast<int>(ub - tiles.begin()) - 1;
      const int covered = length_presums[last_index + 1] - length_presums[i] - max(0, (tiles[last_index].back() - end));
      ret = max(ret, covered);
    }
    return ret;
  }
};
