class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        sort(tiles.begin(), tiles.end());

        int left = 0;
        int right = 0;
        int ans = 0;
        int cover = 0;

        for(right = 0; ans < carpetLen and right < tiles.size(); ){
            //Carpet starts from left tiles

            //If the carpet cover the current tile then it length will be 
            //tiles[right][1] - tiles[left][0] + 1
            if((tiles[right][1] - tiles[left][0] + 1) <= carpetLen){
                //We can cover the whole tile
                cover += (tiles[right][1] - tiles[right][0] + 1);
                ans = max(ans, cover);
                right++;
            }else{
                //We cannot cover the whole tile
                int partial = max(0, tiles[left][0] + carpetLen - tiles[right][0]);
                ans = max(ans, cover + partial);

                //Move the left point of the carpet to the next tile
                cover -= (tiles[left][1] - tiles[left][0] + 1);
                left++;
            }
        }

        return ans;
    }
};
