class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        int n = tiles.size();
        vector<int> pref(n);
        sort(tiles.begin(),tiles.end());
        pref[0] = (tiles[0][1]-tiles[0][0])+1;
        for(int i=1;i<n;i++)
        {
            pref[i] = pref[i-1]+(tiles[i][1]-tiles[i][0])+1;
        }
        vector<int> en(n);
        for(int i=0;i<n;i++)
        {
            en[i] = tiles[i][1];
        }
        int ans = 0;
        for(int i=0;i<n;i++)
        {
            int maxl = tiles[i][0]+carpetLen-1;
            int idx = upper_bound(en.begin(),en.end(),maxl)-en.begin();
            if(idx<n)
            {
                int lower = tiles[idx][0];
                int upper = tiles[idx][1];
                int sum = 0;
                if(idx-1>=0)
                {
                    if(i-1>=0)
                    sum = pref[idx-1]-pref[i-1];
                    else
                    sum = pref[idx-1];
                }
                if(maxl>=lower)
                {
                    sum+=maxl-lower+1;
                }
                ans = max(ans,sum);
            }
            else
            {
                int sum = 0;
                if(i-1>=0)
                {
                    sum = pref[n-1]-pref[i-1];
                }
                else
                sum = pref[n-1];
                ans = max(ans,sum);
            }
        }
        return ans;
    }
};
