func maximumWhiteTiles(tiles [][]int, carpetLen int) int {
	if carpetLen == 1 {
		return 1
	}
	sort.Slice(tiles, func(i, j int) bool {
		return tiles[i][0] < tiles[j][0]
	})
	// left bound index of tiles
	lIndex := 0
	curCover, maxCover := 0, 0
	for _, v := range tiles {
		// longer than or equal to carpetLen
		// return carpetLen directly
		if v[1]-v[0]+1 >= carpetLen {
			return carpetLen
		}
		// lBound, included
		lBound := v[1] - carpetLen + 1
		for tiles[lIndex][1] < lBound {
			curCover -= tiles[lIndex][1] - tiles[lIndex][0] + 1
			lIndex++
		}
		if tiles[lIndex][0] < lBound {
			curCover -= lBound - tiles[lIndex][0]
			tiles[lIndex][0] = lBound
		}
		curCover += v[1] - v[0] + 1
		if curCover > maxCover {
			maxCover = curCover
		}
	}
	return maxCover
}
