class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& a, int t) {
        sort(a.begin(),a.end());

        int ans=0,n=a.size();
        vector<int> cnt(n);

        cnt[0]=a[0][1]-a[0][0]+1;
        if(cnt[0]>=t)
            return t;
        ans=max(ans,cnt[0]);

        for(int i=1;i<n;i++){
            int curr=a[i][1]-a[i][0]+1;
            if(curr>=t)
                return t;
            ans=max(ans,curr);
            cnt[i]=cnt[i-1]+curr;
        }

        for(int i=0;i<n;i++){
            int rt=a[i][0]+t-1;
            if(a[n-1][1]<=rt){
                int p=0;
                if(i)
                    p=cnt[i-1];
                ans=max(ans,cnt[n-1]-p);
                return ans;
            }

            int mx=i,l=i,r=n-1;
            while(l<=r){
                int m=l+((r-l)>>1);
                if(a[m][0]<=rt){
                    l=m+1;
                    mx=max(mx,m);
                }
                else
                    r=m-1;
            }

            if(mx>i){
                int p=0;
                if(i)
                    p=cnt[i-1];
                int cd=cnt[mx-1]-p+min(rt,a[mx][1])+1-a[mx][0];
                ans=max(ans,cd);
            }
        }

        return ans;
    }
};
