        Arrays.sort(t,(a,b)->(a[0]-b[0]));
        int n = t.length;
        int[] sum = new int[n+1];
        for(int i=1;i<=n;i++)
            sum[i] = sum[i-1] + t[i-1][1] - t[i-1][0] + 1;

        int ans = 0;
        for(int i = 0;i <n;i++){
            int l = i, r = n-1, target = t[i][0]+c-1;
            int index = -1;
            while(l<=r){
                int mid = (l+r)/2;
                if(t[mid][0]<=target){
                    l = mid + 1;
                    index = mid;
                }else{
                    r = mid - 1;
                }
