            int l = i, r = n-1, target = t[i][0]+c-1;
            int index = -1;
            while(l<=r){
                int mid = (l+r)/2;
                if(t[mid][0]<=target){
                    l = mid + 1;
                    index = mid;
                }else{
                    r = mid - 1;
                }
            }
            int tmp = sum[index] - sum[i];
            if(target>t[index][1]){
                tmp += t[index][1] - t[index][0] + 1;
            }else{
                tmp += target - t[index][0] + 1;
            }
            ans = Math.max(ans, tmp);
        }

        return ans;
    }
