* ***Using Prefix Sum && Binary Search***

* ***Time Complexity :- O(NlogN)***

* ***Space Complexity :- O(N)***

```
class Solution {
public:

    // function for finding the upper bound of the val

    int upper_bound(vector<vector<int>>& arr, int low, int n, int val)
    {
        int high = n - 1;

        int ans = n;

        while(low <= high)
        {
            int mid = low + (high - low) / 2;

            if(arr[mid][0] > val)
            {
                ans = mid;

                high = mid - 1;
            }
            else
            {
                low = mid + 1;
            }
        }

        return ans;
    }

    int maximumWhiteTiles(vector<vector<int>>& arr, int len) {

        int n = arr.size();

        // sort the array 

        sort(arr.begin(), arr.end());

        // find the prefix sum

        vector<int> prefix(n, 0);

        prefix[0] = arr[0][1] - arr[0][0] + 1;

        for(int i = 1; i < n; i++)
        {
            prefix[i] = prefix[i - 1] + arr[i][1] - arr[i][0] + 1;
        }

        // traverse each interval and find the maxi

        int maxi = 0;

        for(int i = 0; i < n; i++)
        {
            // find the upper bound

            int idx = upper_bound(arr, i + 1, n, (arr[i][0] + len - 1));

            idx--;

            // find the sum between [i to idx]

            int sum = 0;

            if(i > 0)
            {
                sum -= prefix[i - 1];
            }

            if(idx > 0)
            {
                sum += prefix[idx - 1]; 
            }

            // include the sum of the last interval according to condition

            sum += min(arr[idx][1], arr[i][0] + len - 1) - arr[idx][0] + 1;

            // update maxi

            maxi = max(maxi, sum);
        }

        return maxi;
    }
};
