class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        sort(tiles.begin(), tiles.end());

        int empty = 0;

        int i=0, j=0,ans = 1, n = tiles.size();

        while(j<n){

            if(tiles[j][1] - tiles[j][0] + 1 >= carpetLen)
                return carpetLen;

            while(i<j && tiles[j][1] - tiles[i][0] + 1 > carpetLen){
                ans = max(ans, carpetLen - empty);
                empty -= tiles[i+1][0]-tiles[i][1]-1;
                i++;
            }

            while(j<n && tiles[j][1] - tiles[i][0] +1 <= carpetLen){
                 ans = max(ans, tiles[j][1] - tiles[i][0] + 1 - empty);

                if(j<n-1)
                    empty += tiles[j+1][0]-tiles[j][1]-1;

                j++;
            }

        }

        return ans;
    }
};
