    // sliding window
    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {
        int res = 0, l = 0, r = 0, curCover = 0;
        // sort the tiles by its starting index(ascending order)
        // O(NlogN) -> N == tiles.length
        Arrays.sort(tiles, (a,b) -> a[0] - b[0]);

        // iterate from left to right, O(N)
        // It's always optimal to cover carpet at the starting index of any white tile range
        while(r < tiles.length){
            // carpet can cover current white tile range
            // must be ">", not ">=", [1,5], carpetLen = 4 
            // -> white tiles length == 5, 4 is not enough to cover the whole white tiles
            if(tiles[l][0] + carpetLen > tiles[r][1]){
                // update covered length
                curCover += tiles[r][1] - tiles[r][0] + 1;
                res = Math.max(res, curCover);

                // expand window
                r++;
            } else {
                // the remaining carpet may cover non-white tiles OR partial white tiles
                int partial = Math.max(0, tiles[l][0] + carpetLen - tiles[r][0]);
                // update covered length
                res = Math.max(res, curCover + partial);

                // shrink window
                curCover -= (tiles[l][1] - tiles[l][0] + 1);
                l++;

                // edge case : [1,5],[6,10], carpetLen = 1
                if(l > r){
                    // reset window
                    r = l; 
                    curCover = 0;
                } 
            }
        }
        return res;
    }
