import scala.collection.immutable.Queue

case class Interval(from: Int, to: Int) {
  def size = coverSize(to)
  def coverSize(till: Int) = math.max(0, till - from + 1)
}

def maximumWhiteTiles(tiles: Array[Array[Int]], carpetLen: Int): Int = {
  val sorted = tiles.collect { case Array(from , to) => Interval(from, to) }.sortBy(_.from).toList
  if (sorted.exists(_.size >= carpetLen)) carpetLen
  else {
    val coverTo = sorted.head.from + carpetLen - 1
    val covered = Queue.from(sorted.takeWhile(_.to <= coverTo))
    val coverSum = covered.map(_.size).sum
    go(carpetLen - 1, covered, coverSum, Int.MinValue, sorted.drop(covered.size))
  }
}

def go(carpetLen: Int, covered: Queue[Interval], coverSum: Int, maxSeen: Int, remaining: List[Interval]): Int =
  remaining match {
    case Nil => math.max(maxSeen, coverSum)
    case h :: tail =>
      val nMaxSeen = math.max(maxSeen, coverSum + h.coverSize(covered.head.from + carpetLen))
      val (deqeueued, queue) = covered.dequeue

      val nCoverTo = queue.headOption.getOrElse(h).from + carpetLen
      val nCovered = remaining.takeWhile(_.to <= nCoverTo)
      val nCoverSum = coverSum + nCovered.map(_.size).sum - deqeueued.size

      go(carpetLen, queue.enqueueAll(nCovered), nCoverSum, nMaxSeen, remaining.drop(nCovered.size))
  }
