class Solution {
public:
    int getIdxAfter(int val, vector<vector<int>> &tiles){
        int lo=0, hi=tiles.size()-1, mid;
        while(lo<hi){
            mid=lo+(hi-lo)/2;
            if(tiles[mid][0]>val)
                hi=mid;
            else
                lo=mid+1;
        }
        return lo;
    }

    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        int n=tiles.size(), endPoint, idx, lenCovered, res=0;
        vector<int> prefixSum(n+1,0);
        sort(tiles.begin(), tiles.end());
        tiles.push_back({INT_MAX, INT_MAX});

        //cal prefix sum
        for(int i=1;i<=n;i++)
            prefixSum[i] = prefixSum[i-1]+(tiles[i-1][1]-tiles[i-1][0]+1);

        for(int i=0;i<n;i++){
            endPoint = tiles[i][0]+carpetLen-1;
            idx = getIdxAfter(endPoint, tiles);
            lenCovered = prefixSum[idx]-prefixSum[i] - max(0, tiles[idx-1][1]-endPoint);
            res=max(res, lenCovered);
        }
        return res;
    }
};
