public class Solution {
    public int MaximumWhiteTiles(int[][] tiles, int carpetLen) {
        int l =0, r =0, cur =0,ans = int.MinValue;
        Array.Sort(tiles, (x,y)=>x[0] - y[0]);
        while(r<tiles.Length)
        {
            cur += tiles[r][1] - tiles[r][0] + 1;//Pull the carpet over the new range of tiles. Add the whole range to result.
            int carpetLeft = tiles[r][1] - carpetLen + 1; // Find out the left end of carpet
            while (l<= r && tiles[l][0] < carpetLeft) // Remove every range that is not fully covered. Partially covered will be handled later
            {
                cur -= tiles[l][1] - tiles[l][0] + 1;
                l++;
            }
            int leftoverlap = 0;
            if (l > 0 && tiles[l - 1][1] >= carpetLeft)// For the range on left of l, it could be paritlly covered. Add the partial amount to result
                leftoverlap = tiles[l - 1][1] - carpetLeft + 1;
            ans = Math.Max(ans, cur + leftoverlap);
            r++;
        }
        return ans;
    }
}
