/**
 * @param {number[][]} tiles
 * @param {number} carpetLen
 * @return {number}
 */
var maximumWhiteTiles = function(tiles, carpetLen) {
    tiles.sort((A, B) => A[0] - B[0])
    const len = tiles.length
    const sumSF = new Array(len)
    sumSF[-1] = 0


    for (const [i, tile] of tiles.entries()) {
        const size = 1 + tile[1] - tile[0]
        sumSF[i] = size + sumSF[i - 1]
    }


    let result = 0
    for (const [i, tile] of tiles.entries()) {
        const [from, to] = tile
        let low = 0, high = -1 + len
        const last = tiles[high]

        const lastTo = last[1]
        if (1 + lastTo - from <= carpetLen) {
            result = Math.max(result, sumSF[high] - sumSF[-1 + i])
            break
        }


        // find earliest high where
        // tiles[high] is enough to hold the carpet
        while (low < high) {
            const mid = Math.floor((low + high) / 2)
            const midSize = 1 + tiles[mid][1] - from
            if (midSize >= carpetLen)
                high = mid
            else
                low = 1 + mid
        }

        if (tiles[high][0] > -1 + from + carpetLen) {
            const outcome = sumSF[high - 1] - sumSF[-1 + i]
            result = Math.max(result, outcome)
            continue
        }

        let outcome = sumSF[high] - sumSF[-1 + i]
        const wasted = tiles[high][1] - (-1 + from + carpetLen)
        outcome -= wasted

        result = Math.max(result, outcome)
    }


    return result
};
