class Solution {
    public int maximumWhiteTiles(int[][] tiles, int target) {
        // Sort the array in required format
        Arrays.sort(tiles, (a,b)->{
            return a[0]==b[0] ? a[1]-b[1]:a[0]-b[0];
        });

        // make prefix sum array to store the total covered area till ith index
        int[] prefix_sum = new int[tiles.length+1];
        for(int i = 1; i<=tiles.length; i++){
            prefix_sum[i] = prefix_sum[i-1] + tiles[i-1][1] - tiles[i-1][0] + 1;
        }

        int max = 0;
        for(int i = 1; i<=tiles.length; i++){
            // get the index, from where the ith index can reach
            int idx = binary_search(tiles, tiles[i-1][0] + target, i-1, tiles.length-1);

            // calculate the sum till the idx index
            int prefix = (prefix_sum[idx] - prefix_sum[i-1]);

            // taking into account the ith index, and calculate if more area can be covered in idx or not.
            int remaining = Math.min(target - (tiles[idx][0] - tiles[i-1][0]), prefix_sum[idx+1]-prefix_sum[idx]);

            // this is the total possible units that can be covered from ith index
            int tmp = prefix + (Math.max(0, remaining));


            // System.out.println(i + " " + idx + " " + tmp + " (" + prefix + ", " + remaining + ")");
            // This will bring us the maximum for all such indexes
            max = Math.max(max, tmp);
        }

        return max;
    }


    // This is the basic binary search which will give us the index idx, such that the target from ith index strickly lies between idx.
    public int binary_search(int[][] tiles, int target, int l, int h){
        while(l<h){
            int mid = l + (h-l)/2;
            if(tiles[mid][1]>=target){
                h = mid;
            }
            else{
                l = mid+1;
            }
        }
        return l;
    }
