class Solution {
public:
    int getSize(tuple<int,int,int> t) {
        return get<1>(t) - get<0>(t) + 1;
    }

    bool is_carpet(tuple<int,int,int> t) {
        return get<2>(t) == 1;
    }

    int solve(int carpetLen, vector<tuple<int,int,int>> ranges) {
        int current_size = 0;
        int current_carpet_size = 0;

        int windowStart = 0;
        int sol = 0;
        for (int i = 0; i < ranges.size(); ++i) {
            current_size += getSize(ranges[i]);

            if (is_carpet(ranges[i])) {
                current_carpet_size += getSize(ranges[i]);
                sol = max(sol, current_carpet_size - max(0, current_size - carpetLen));
            }

            while (windowStart <= i && current_size >= carpetLen) {
                current_size -= getSize(ranges[windowStart]);

                if (is_carpet(ranges[windowStart]))
                    current_carpet_size -= getSize(ranges[windowStart]);

                windowStart++;

                if (is_carpet(ranges[i])) 
                    sol = max(sol, current_carpet_size - max(0, current_size - carpetLen));
            }
        }

        return sol;
    }

    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        vector<tuple<int,int,int>> ranges;

        sort(tiles.begin(), tiles.end());
        for (int i = 0; i < tiles.size(); ++i) {
            // Add gaps.
            if (i > 0 && tiles[i-1][1] + 1 <= tiles[i][0] - 1)
                ranges.push_back({tiles[i-1][1] + 1, tiles[i][0] - 1, 0});

            ranges.push_back({tiles[i][0], tiles[i][1], 1});
        }

        return solve(carpetLen, ranges);
    }
};
