           int l=tiles[i][0];
           int r=tiles[i][0]+carpetlen-1;
           int idx=upper_bound(endd.begin(),endd.end(),r)-endd.begin();
           //idx which is strictly greater than r
            int tt;
            if(idx>=tiles.size()){
                tt=prefix[idx-1];
            }
            else{
                tt=prefix[idx];
                if(r>=tiles[idx][0] and r<=tiles[idx][1])
