class Solution {
public:
    static bool cmp(vector<int> &a,vector<int> &b){
        return a[1]<b[1];
    }
    int bs(int end,vector<vector<int>> &tiles,int strt){
        if(end<=tiles[strt][1])
            return strt;
         int l=strt;
        int r=tiles.size()-1;
        int ans=-1;
        while(l<=r){
            int mid=l+(r-l)/2;
            if(tiles[mid][0]>end)
                r=mid-1;
            else if(tiles[mid][1]<end){
                ans=mid;
                l=mid+1;
            }
            else
                return mid;
            }
        return ans;
    }
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        sort(tiles.begin(),tiles.end(),cmp);
        int n=tiles.size();
        vector<int> dp(n);
        int maxi=0;
        dp[0]=tiles[0][1]-tiles[0][0]+1;
        for(int i=1;i<n;i++)
            dp[i]=dp[i-1]+tiles[i][1]-tiles[i][0]+1;
        for(int i=0;i<n;i++){
            int end=tiles[i][0]+carpetLen-1;
            int idx=bs(end,tiles,i);
            int res=0;
            if(idx && i)
               res=dp[idx-1]-dp[i-1];
            else if(idx)
                res=dp[idx-1];
            res+=min(end-tiles[idx][0]+1,tiles[idx][1]-tiles[idx][0]+1);
            maxi=max(maxi,res);
        }
        return maxi;
    }
};
