class Solution {
public:
    // Time Complexity:- O(NlogN)
    // Space Complexity:- O(N)
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        int n=tiles.size();
        sort(tiles.begin(),tiles.end());//sorting the array
        vector<int>pref(n);
        //then storing prefix sum
        int ans=INT_MIN;
        for(int i=0;i<n;i++){
            pref[i]=tiles[i][1]-tiles[i][0]+1;
            if(i>0){
                pref[i]+=pref[i-1];
            }
        }
        for(int i=0;i<n;i++){
            int leftend=tiles[i][0],rightend=tiles[i][0]+carpetLen-1;
            //if we start spreading the carpet from first tiles so we start from tile[i][0] to the tiles[i][0]+carpetlen-1
            //example if start from [1,5 ]
            //leftend=1,rightend=1+10-1=10
            //now find how many tiles we fully covered between leftend to right end 
            //for finding this we use binary search
            int l=i,r=n-1,left=i,right=i-1;
            while(l<=r){
                int mid=l+(r-l)/2;
                if(tiles[mid][1]<=rightend){
                    //we check if that tiles is coming under our rightend if it come under rightend that means we covered that tile fully
                    right=mid;
                    l=mid+1;//check for next tile in thr right
                }
                else{
                    r=mid-1;//check in left tile
                }
            }
            int curr=0;
            if(right!=i-1){
                //now we check that we find any tile or not if didn't able to find any tile our right remains i-1 or if we find any tile we stored that index in right
                curr+=pref[right];//store the sum of tiles till that index
                if(left>0){
                    curr-=pref[left-1];//because we have to decrease the sum of tile befor our new left end because we start from new leftend
                    //example if start from 1 we have to decrease the pref sum befor thr 1 index
                }
            }
            if(right+1<n){//if we cover full area of some tile and half area of next one tile 
                curr+=max(0,rightend-tiles[right+1][0]+1);//
            }
            ans=max(ans,curr);

        }
        return ans;
    }
};
