use std::cmp::max;
impl Solution {
    pub fn maximum_white_tiles(tiles: Vec<Vec<i32>>, carpet_len: i32) -> i32 {
        let mut tiles = tiles;
        tiles.sort();
        let n = tiles.len();
        let mut j = 0;
        let mut curr = 0;
        let mut ans = 0;

        for i in 0..n {
            let t0 = &tiles[i];
            let start = t0[0];
            while j < n && tiles[j][1] < start + carpet_len {
                curr += tiles[j][1] - tiles[j][0] + 1;
                j += 1;
            }
            let mut ext = 0;
            if j < n && tiles[j][0] <= start + carpet_len {
                ext = start + carpet_len - tiles[j][0];
            }

            ans = max(curr + ext, ans);
            curr -= tiles[i][1] - tiles[i][0] + 1;
        }
        ans
    }
}
