class Solution {
    public int maximumWhiteTiles(int[][] tiles, int carpetLen) {
        Arrays.sort(tiles, (a, b) -> a[0] - b[0]);
        int n = tiles.length;
        int ans =0;
        int[] pst = new int[n]; //presum for tiles
        pst[0] = tiles[0][1] - tiles[0][0] + 1;
        for(int i=1;i<n;i++)
        {
            pst[i] = pst[i-1] + (tiles[i][1] - tiles[i][0] + 1);
        }

        for(int i=0;i<n;i++)
        {
            int l=i;
            int r=n-1;
            int rightbound = tiles[i][0] + carpetLen - 1;
            int fcs = i-1; // fully covered section of tile

            while(l<=r)
            {
                int mid = (l+r)/2;
                if(tiles[mid][1] <= rightbound)
                {
                    fcs = mid;
                    l=mid+1;
                }
                else
                    r=mid-1;
            }
            int cot = 0; //count of tiles when starting from index i

            if(fcs != i-1)
            {
                cot += pst[fcs];
                if(i>0)
                    cot -= pst[i-1];
            }
            // check for partial covered tiles
            if(fcs+1<n)
            {
                cot += Math.max(0, rightbound - tiles[fcs+1][0]+1);
            }

            ans = Math.max(ans,cot);
        }
        return ans;
    }
}

