class Solution {
public:
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        sort(tiles.begin(), tiles.end());
        int n = tiles.size(), res = 0, cnt[n];
        cnt[0] = 0;
        if (tiles[0][1] - tiles[0][0] + 1 >= carpetLen) return carpetLen;
        for (int i = 1; i < n; ++i) {
            if (tiles[i][1] - tiles[i][0] + 1 >= carpetLen) return carpetLen;
            cnt[i] = cnt[i - 1] + tiles[i][0] - tiles[i - 1][1] - 1;
        }
        for (int i = 0; i < n; ++i) {
            auto it = lower_bound(tiles.begin() + i, tiles.end(), vector<int>{tiles[i][0] + carpetLen - 1, -1});
            if (it != tiles.begin()) {
                if (it != tiles.end()) {
                    if ((*it)[0] > tiles[i][0] + carpetLen - 1) --it;
                    res = max(res, min((*it)[1] - tiles[i][0] + 1, carpetLen) - (cnt[distance(tiles.begin(), it)] - cnt[i]));
                }
                else {
                    res = max(res, min(tiles[n - 1][1] - tiles[i][0] + 1, carpetLen) - (cnt[n - 1] - cnt[i]));
                }
            }
        }
        return res;
    }
};
