            auto it = lower_bound(tiles.begin() + i, tiles.end(), vector<int>{tiles[i][0] + carpetLen - 1, -1});
            if (it != tiles.begin()) {
                if (it != tiles.end()) {
                    if ((*it)[0] > tiles[i][0] + carpetLen - 1) --it;
                    res = max(res, min((*it)[1] - tiles[i][0] + 1, carpetLen) - (cnt[distance(tiles.begin(), it)] - cnt[i]));
                }
                else {
                    res = max(res, min(tiles[n - 1][1] - tiles[i][0] + 1, carpetLen) - (cnt[n - 1] - cnt[i]));
                }
            }
        }
        return res;
    }
};
