class Solution {
public:
    static bool mycmp(vector<int>& v1, vector<int>& v2)
    {
        return (v1[0]<v2[0]);
    }
    int bsearch(vector<vector<int>>& tiles, int target)
    {
        //find after which index target is lying
        //or upto what index we can consider all blocks of tiles

        int start=0,end=tiles.size()-1;
        int ans=-1;

        while(start<=end)
        {
            int mid=start+(end-start)/2;
            if(target>=tiles[mid][1])
            {
                ans=mid;
                start=mid+1;
            }
            else
            {
                end=mid-1;
            }
        }
        return ans;
    }
    int maximumWhiteTiles(vector<vector<int>>& tiles, int carpetLen) {
        //sort the tiles
        //find in every range

        sort(tiles.begin(),tiles.end(),mycmp);

        //try to put carpet at starting of tile block (from both ends) -> greedy  (only this can give maximum occupied tiles)
        // O(NlogN) -> try to think of binary search

        //prefix sum and binary search
        int n=tiles.size();
        vector<int> prsum(n,0);
        prsum[0]=tiles[0][1]-tiles[0][0]+1;

        for(int i=1;i<n;i++)
        {
            int current=tiles[i][1]-tiles[i][0]+1;
            prsum[i]=prsum[i-1]+current;
        }

        int ans=0;
        for(int i=0;i<n;i++)
        {
            int start=tiles[i][0];  //inclusive
            int end=tiles[i][0]+carpetLen-1;  //inclusive

            int index=bsearch(tiles,end);
            if(index==-1)
            {
                int total=(end-tiles[index+1][0]+1);
                ans=max(ans,total);
            }
            else
            {
                int total= (i==0)?prsum[index]:prsum[index]-prsum[i-1];
                if(index<n-1)
                {
                    if(end>=tiles[index+1][0])
                    {
                        total+=(end-tiles[index+1][0]+1);
                    }
                }
                ans=max(ans,total);
            }

        }
        return ans;



    }
};
