    int maximumWhiteTiles(vector<vector<int>>& t, int c) {

        sort(t.begin(),t.end());

        vector<int> v;
        v.push_back(0);
        for(int i = 1;i<t.size();i++)
        {
            v.push_back(t[i][0]-t[i-1][1]-1);//storing range of not white tiles 
        }

        for(int i=1;i<v.size();i++)
        {
            v[i]+=v[i-1]; // prefix sum
        }
        vector<int> sec_sec;
        for(int i=0;i<t.size();i++)
