        int res = 0;
        for(int i=0;i<t.size();i++)
        {

            int ans = 0;
            int tk = t[i][0] + c - 1;// total length that could be cover from current index

            int idx = lower_bound(sec_sec.begin(),sec_sec.end(),tk) - sec_sec.begin();//max index till where it could cover

            if(idx==sec_sec.size())
            {
                idx--;

