func getPre(list [][]int, a int) int {
    if a <= list[0][0] {
        return 0
    }

    j := sort.Search(len(list), func(i int) bool {
        return list[i][0] > a
    }) - 1

    if list[j][1] < a {
        a = list[j][1] + 1
    }

    return list[j][2] + a - list[j][0] 
