object Solution {
  def maximumWhiteTiles(tiles: Array[Array[Int]], carpetLen: Int): Int = {
    // sorts the groups of white tiles by their starting points
    tiles.sortInPlaceBy(_.head)

    /**
     * @return the length of the group of white tiles at the given index
     */
    def length(i: Int) = tiles(i)(1) - tiles(i)(0) + 1

    // The state is (i, j, numTiles) where:
    // - [i, j) is the indices of the groups of white tiles that the carpet is covering
    // - numTiles is the sum length of the groups of white tiles that the carpet is fully covering
    Iterator
      .unfold(0, 0, 0) {
        case (i, j, _) if i >= tiles.length || j >= tiles.length =>
          None
        // carpet does not cover any of the group of white tiles at index j
        case (i, j, numTiles) if carpetLen <= tiles(j)(0) - tiles(i)(0) =>
          // moves the start of the carpet to the next group
          Some(numTiles, (i + 1, j, numTiles - length(i)))
        // carpet partially covers the group of white tiles at index j
        case (i, j, numTiles) if carpetLen <= tiles(j)(1) - tiles(i)(0) =>
          // moves the start of the carpet to the next group
          Some(numTiles + tiles(i)(0) + carpetLen - tiles(j)(0), (i + 1, j, numTiles - length(i)))
        // carpet extends past the group of white tiles at index j
        case (i, j, numTiles) =>
          // moves the end of the carpet to the next group
          Some(numTiles + tiles(j)(1) - tiles(j)(0) + 1, (i, j + 1, numTiles + length(j)))
      }
      .maxOption
      .getOrElse(0)
  }
}
