The code comes from the above math formula:

```
class Solution {
public:
    int totalStrength(vector<int>& strength) {
        int n = strength.size();
        int mod = int(1e9 + 7);
        vector<long long> P(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            P[i + 1] = (P[i] + strength[i]) % mod;
        }
        vector<long long> Q(n + 2, 0);
        for (int i = 0; i <= n; ++i) {
            Q[i + 1] = (Q[i] + P[i]) % mod;
        }
        vector<pair<int, int>> stack;
        stack.emplace_back(-1, INT_MIN);
        vector<int> A(n, 0);
        for (int i = 0; i < n; ++i) {
            while (!stack.empty() && stack.back().second > strength[i]) {
                stack.pop_back();
            }
            A[i] = i - stack.back().first - 1;
            stack.emplace_back(i, strength[i]);
        }
        stack.clear();
        stack.emplace_back(n, INT_MIN);
        vector<int> B(n, 0);
        for (int i = n - 1; i >= 0; --i) {
            while (!stack.empty() && stack.back().second >= strength[i]) {
                stack.pop_back();
            }
            B[i] = stack.back().first - i - 1;
            stack.emplace_back(i, strength[i]);
        }
        int ans = 0;
        for (int i = 0; i < n; ++i) {
            long long X = 1L * (A[i] + 1) * (0L + Q[i + B[i] + 2] - Q[i + 1]) % mod;
            long long Y = 1L * (B[i] + 1) * (0L + Q[i + 1] - Q[i - A[i]]) % mod;
            ans = (0L + ans + 1L * strength[i] * (X - Y) + mod) % mod;
        }
        return ans;
    }
};
```

