// time O(n) space O(n)
class Solution {
	private static final int MOD = 1_000_000_007;

	public int totalStrength(int[] strength) {
		int n = strength.length;
		long[] preFix = preSumOfPreSum(strength);
		int[] nse = nextSmallerOrEqual(strength);
		int[] ps = prevSmaller(strength);

		long ret = 0;
		for (int i = 0; i < n; ++i) {
			// ret += (long)strength[i] * 
			//     ((preFix[nse[i]+1] - preFix[i+1])*(i-ps[i]) - 
			//      (preFix[i+1] - preFix[ps[i]+1])*(nse[i]-i));
			ret += strength[i] * 
				modSubtract(modSubtract(preFix[nse[i]+1], preFix[i+1]) * (i - ps[i]) % MOD,
						   modSubtract(preFix[i+1], preFix[ps[i]+1]) * (nse[i]-i) % MOD);
			ret %= MOD;
		}

		return (int)ret;
	}

	private long modSubtract(long a, long b) {
		if (a < b) return a + MOD - b;
		return a - b;
	}

	private int[] prevSmaller(int[] nums) {
		int n = nums.length;
		int[] ret = new int[n];
		Arrays.fill(ret, -1);
		int min = nums[0];
		for (int i = 1; i < n; ++i) {
			if (nums[i] <= min) {
				min = nums[i];
				continue;
			}

			int j = i - 1;
			while (nums[j] >= nums[i]) {
				j = ret[j];
			}
			ret[i] = j;
		}
		return ret;

		// return new int[]{-1, 0, -1, 2};
	}

	private int[] nextSmallerOrEqual(int[] nums) {
		int n = nums.length;
		int[] ret = new int[n];
		Arrays.fill(ret, n);
		int min = nums[n-1];
		for (int i = n-2; i >= 0; --i) {
			if (nums[i] < min) {
				min = nums[i];
				continue;
			}

			int j = i + 1;
			while (nums[j] > nums[i]) {
				j = ret[j];
			}
			ret[i] = j;
		}
		return ret;

		// return new int[]{2,2,4,4};
	}

	private long[] preSumOfPreSum(int[] nums) {
		int n = nums.length;
		long[] preSum = new long[n + 1];
		for (int i = 0; i < n; ++i) {
			preSum[i + 1] = preSum[i] + nums[i];
			preSum[i + 1] %= MOD;
		}
		long[] preSumOfPreSum = new long[n + 2];
		for (int i = 0; i < n + 1; ++i) {
			preSumOfPreSum[i + 1] = preSumOfPreSum[i] + preSum[i];
			preSumOfPreSum[i + 1] %= MOD;
		}
		return preSumOfPreSum;
	}
}
