    int lg[100009];
int N;
void build(){
    lg[1] = 0;
for (int i = 2; i <= 100003; i++)
    lg[i] = lg[i/2] + 1;

for (int i = 0; i < N; i++)
    st[i][0] = arr[i];

for (int j = 1; j <= K; j++)
    for (int i = 0; i + (1 << j) <= N; i++)
        st[i][j] = min(st[i][j-1], st[i + (1 << (j - 1))][j - 1]);
}

